sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

// Get time and date 1 day later
	var hours24 = 60*60*24;
	var soonDate = rightNow.addSeconds(hours24);

// a State field value of Closed Complete.
	var soondueNITasks = new GlideRecord('x_58872_needit_needit_task');
	soondueNITasks.addQuery('due_date','>=',rightNow);
	soondueNITasks.addQuery('due_date','<=',soonDate);
	soondueNITasks.addQuery('state','!=',3);
//	soondueNITasks.query();

// Write a log message for each overdue NeedIt Task Record
	gs.info("Executing 'Find NeedIt Tasks Due Soon' script with query that I don't know how to extract to a log");
	//gs.info("Executing 'Find NeedIt Tasks Due Soon' script with query = " + soondueNITasks.addQuery);
	while(soondueNITasks.next()){
		gs.info("NeedIt Task record due in next 24 hours = " + soondueNITasks.number);
